package com.mohey.websocketservice;import lombok.AccessLevel;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@NoArgsConstructor(access = AccessLevel.PRIVATE)@AllArgsConstructor@Builderpublic class ChatMessage {	private String type;	private String roomId;	private String sender;	private String message;	public void newConnect() {this.type = "new";}	public void closeConnect() {this.type = "close";}}