package com.mohey.websocketservice.dto;import java.util.List;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;import lombok.AccessLevel;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@Document(collection = "chatting_member")@NoArgsConstructor@AllArgsConstructorpublic class ChatMember {	@Id	@Field("member_uuid")	private String memberUuid;	@Field("groups")	private List<Group> groups;}