package com.mohey.websocketservice.dto;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;import lombok.AccessLevel;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.ToString;//@Getter@NoArgsConstructor(access = AccessLevel.PRIVATE)@AllArgsConstructor//@Builder//@ToString@Data@Document(collection = "chatting_message")	//(collection = "chatting_message")public class ChatMessage {	@Id	private int id;	@Field("room_id")	private String roomId;	@Field("type")	private String type;	@Field("sender_name")	private String senderName;	@Field("sender_uuid")	private String senderUuid;	@Field("message")	private String message;	@Field("send_time")	@CreatedDate	private String sendTime;	public void newConnect() {this.type = "new";}	public void closeConnect() {this.type = "close";}}