package com.mohey.websocketservice.dto;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;import lombok.AccessLevel;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@Document(collection = "chatting_room")@NoArgsConstructor(access = AccessLevel.PRIVATE)@AllArgsConstructorpublic class ChatRoom {	@Field("group_uuid")	private String groupId;	@Field("group_name")	private String groupName;	@Field("group_type")	private String groupType;}