package com.mohey.websocketservice.dto;import java.time.LocalDateTime;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Field;import org.springframework.web.multipart.MultipartFile;import lombok.AccessLevel;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@NoArgsConstructor@AllArgsConstructor@Datapublic class ReceiveMessage {	private String groupId;	private String senderName;	private String senderUuid;	private String type;	private String message;	private MultipartFile image;	private String imageUrl;	@Field("send_time")	@Indexed	private LocalDateTime sendTime;}